import React,{useState} from 'react';
import Boton from './Boton';

//VARIABLES
let numero1=null,numero2=null,operador='';
const historia = [];

//FUNCION PRINCIPAL
const Calculadora = () =>{
    //ESTADO
    const [resultado,setResultado] = useState('0');
    const [operacion,setOperacion] = useState('');

    //FUNCIONES POR NUMERO
    const Numero1 = (props) =>{
        setResultado('');
        setResultado(`${resultado}${props}`);
        if(props === '+' ||props === '-'||props === '/'||props === '*')
        {
            operador = props;
            setOperacion(`${numero1} ${operador}`);
            setResultado('');
        }
        else if(numero1 != null)
        {
            numero1 = numero1+props;
        }
        else numero1 = props;
    }
    const Numero2 = (props) =>{
        setOperacion(`${operacion}${props}`);
        if(props === '=')
        {
            setOperacion(`${operacion} ${props}`);
        }
        else if(numero2 != null)
        {
            numero2 = numero2+props;
        }
        else numero2 = props;
    }
    //FUNCION PRICIPAL DE ASIGANCION DE NUMEROS
    const actualizarResultado =(props)=>{
        if(operacion === '')
        {
            Numero1(props);
        }
        else 
        {
            Numero2(props);
            if(props ==='=')
            {
                iniciarHistoria();
                calcular();
            }
        }
    }
    //LIMPIAR DISPLAY
    const limpiarDisplay = ()=>{
        setResultado('0');
        setOperacion('');
        numero1=null;
        numero2=null;
        operador='';
    }
    //HISTORIAL
    const iniciarHistoria =()=>{
        historia.push(operacion);
    }
    const mostrarHistoria = (bandera=false)=>{
        if(bandera === true)
        {
            return(
                historia.map((item,index)=>{
                    return <li key={index}>{item}</li>;
                })
            );
        }
    }

    //CALCULAR EL RESULTADO
    const calcular =()=>{
        let r;
        switch(operador){
            case '+':
                r = Number(numero1) + Number(numero2);
                setOperacion(`${String(r)}`);
                break;
            case '-':
                r = Number(numero1) - Number(numero2);
                setOperacion(`${String(r)}`);
                break;
            case '*':
                r = Number(numero1) * Number(numero2);
                setOperacion(`${String(r)}`);
                break;
            case '/':
                r = Number(numero1) / Number(numero2);
                setOperacion(`${String(r)}`);
                break;
            default:
                setOperacion(`$error`);
                break;
        }
    }
    //se puede sacar resultado
    return(
        <div class='container d-flex justify-content-center'>
            <div class='col-4'>
                <div id='visor' class='d-flex justify-content-end'>
                    <h3 id='operacion'>{operacion}</h3>
                    <h3 id='operacion'>{resultado}</h3>
                </div>
                <div class='d-flex justify-content-center'>
                    <Boton actualizar={actualizarResultado} limpiar={limpiarDisplay} historia={mostrarHistoria}/>
                </div>
                <div>
                    <ul>
                        {

                        }
                    </ul>
                </div>
            </div>
        </div>
    );
}

export default Calculadora;
