import React,{useState} from 'react';
import Boton from './Boton';

//VARIABLES
let numero1=null,numero2=null,operador='';
const historia = [];

//FUNCION PRINCIPAL
const Calculadora = () =>{
    //ESTADO
    const [resultado,setResultado] = useState('');
    const [operacion,setOperacion] = useState('');

    //FUNCIONES POR NUMERO
    const Numero1 = (props) =>{
        setResultado(`${resultado}${props}`);
        if(props === '+' ||props === '-'||props === '/'||props === '*')
        {
            operador = props;
            setOperacion(`${numero1} ${operador}`);
            setResultado('');
        }
        else if(numero1 != null)
        {
            numero1 = numero1+props;
        }
        else numero1 = props;
    }
    const Numero2 = (props) =>{
        setOperacion(`${operacion}${props}`);
        if(props === '=')
        {
            setOperacion(`${operacion} ${props}`);
        }
        else if(numero2 != null)
        {
            numero2 = numero2+props;
        }
        else numero2 = props;
    }
    //FUNCION PRICIPAL DE ASIGANCION DE NUMEROS
    const actualizarResultado =(props)=>{
        if(operacion === '')
        {
            Numero1(props);
        }
        else 
        {
            Numero2(props);
            if(props ==='=')
            {
                iniciarHistoria();
                calcular();
            }
        }
    }
    //LIMPIAR DISPLAY
    const limpiarDisplay = ()=>{
        setResultado('');
        setOperacion('');
        numero1=null;
        numero2=null;
        operador='';
    }
    //HISTORIAL
    const iniciarHistoria =()=>{
        historia.push(operacion);
    }
    const mostrarHistoria = ()=>{
        return(
            <>
                <ul>
                    {
                        historia.map((item,index)=>{
                            return <li key={index}>Esto funciona</li>
                        })
                    }
                </ul>
            </>
        );
    }

    //CALCULAR EL RESULTADO
    const calcular =()=>{
        let r;
        switch(operador){
            case '+':
                r = Number(numero1) + Number(numero2);
                setOperacion(`${String(r)}`);
                break;
            case '-':
                r = Number(numero1) - Number(numero2);
                setOperacion(`${String(r)}`);
                break;
            case '*':
                r = Number(numero1) * Number(numero2);
                setOperacion(`${String(r)}`);
                break;
            case '/':
                r = Number(numero1) / Number(numero2);
                setOperacion(`${String(r)}`);
                break;
            default:
                setOperacion(`$error`);
                break;
        }
    }

    return(
        <div class>
            <h3>{operacion}</h3>
            <h2>{resultado}</h2>
            <Boton actualizar={actualizarResultado} limpiar={limpiarDisplay} historia={mostrarHistoria}/>
        </div>
    );
}

export default Calculadora;
