import React,{useState} from 'react';
import Boton from './Boton';

const Calculadora = () =>{
    const [resultado,setResultado] = useState('');
    const [operacion,setOperacion] = useState('operacion')
    const historia = [];
    let op ={
        numero1 : null,
        numero2 : null,
        operador : ''
    };

    const actualizarResultado = (props) =>{
        setResultado(resultado+props);
        if(props === '+' ||props === '-'||props === '/'||props === '*')
        {
            op.operador = props;
            setOperacion(`${op.numero1}${op.operador}`);
            setResultado('0');
        }
        else if(op.numero1 != null)
        {
            op.numero1 = op.numero1+props;
        }
        else op.numero1 = props;
        console.log(op.numero1);
    }

    const limpiarDisplay = ()=>{
        setResultado('0');
    }
    const iniciarHistoria =()=>{
        historia.push(operacion);
    }
    return(
        <>
            <h2>{operacion}</h2>
            <h2>{resultado}</h2>
            <Boton actualizar={actualizarResultado} limpiar={limpiarDisplay} historia={iniciarHistoria}/>
        </>
    );
}

export default Calculadora;
