import React,{useState} from 'react';
import Boton from './Boton';

//VARIABLES
let numero1=null,numero2=null,operador='';
const historia = [];

//FUNCION PRINCIPAL
const Calculadora = () =>{
    //ESTADO
    //const [resultado,setResultado] = useState('');
    const [operacion,setOperacion] = useState('');

    //FUNCIONES POR NUMERO
    const Numero1 = (props) =>{
        setResultado(`${resultado} ${props}`);
        if(props === '+' ||props === '-'||props === '/'||props === '*')
        {
            operador = props;
            setOperacion(`${numero1} ${operador}`);
            setResultado('');
        }
        else if(numero1 != null)
        {
            numero1 = numero1+props;
        }
        else numero1 = props;
    }
    const Numero2 = (props) =>{
        setResultado(`${resultado} ${props}`);
        if(props === '=')
        {
            setOperacion(`${operacion} ${resultado} ${props}`);
            setResultado('');
        }
        else if(numero2 != null)
        {
            numero2 = numero2+props;
        }
        else numero2 = props;
    }
    //FUNCION PRICIPAL DE ASIGANCION DE NUMEROS
    const actualizarResultado =(props)=>{
        if(operacion === '')
        {
            Numero1(props);
        }
        else 
        {
            Numero2(props);
            if(props ==='=')
            {
                calcular();
            }
        }
    }
    //LIMPIAR DISPLAY
    const limpiarDisplay = ()=>{
        setResultado('');
        setOperacion('');
    }
    //HISTORIAL
    const iniciarHistoria =()=>{
        historia.push(operacion);
    }
    //CALCULAR EL RESULTADO
    const calcular =()=>{
        let r;
        switch(operador){
            case '+':
                r = Number(numero1) + Number(numero2);
                setOperacion(`${operacion} ${String(r)}`);
                setResultado(r);
                break;
            case '-':
                r = Number(numero1) - Number(numero2);
                setOperacion(`${operacion} ${String(r)}`);
                setResultado(r);
                break;
            case '*':
                r = Number(numero1) * Number(numero2);
                setOperacion(`${operacion} ${String(r)}`);
                setResultado(r);
                break;
            case '/':
                r = Number(numero1) / Number(numero2);
                setOperacion(`${operacion} ${String(r)}`);
                setResultado(r);
                break;
            default:
                setOperacion(`${operacion} error`);
                break;
        }
    }

    return(
        <>
            <h2>{operacion}</h2>
            <h2>{resultado}</h2>
            <Boton actualizar={actualizarResultado} limpiar={limpiarDisplay} historia={iniciarHistoria}/>
        </>
    );
}

export default Calculadora;
