import React,{useState} from 'react';
import Boton from './Boton';

const Calculadora = () =>{
    const [resultado,setResultado] = useState('0');
    const [operacion,setOperacion] = useState('operacion')
    const historia = [];
    let operaci

    const actualizarResultado = (props) =>{
        setResultado(props);
    }
    const limpiarDisplay = ()=>{
        setResultado('0');
    }

    return(
        <>
            <h2>{operacion}</h2>
            <h2>{resultado}</h2>
            <Boton valor='cambiado' actualizar={actualizarResultado} limpiar={limpiarDisplay}/>
        </>
    );
}

export default Calculadora;
